import React from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import * as z from 'zod';

const schema = z.object({
  name: z.string().min(2, { message: "Name must be at least 2 characters" }),
  address: z.string().min(5, { message: "Address must be at least 5 characters" }),
  phone: z.string().regex(/^\d{10}$/, { message: "Phone must be 10 digits" }),
});

const OrderForm = ({ cartItems, placeOrder }) => {
  const { register, handleSubmit, formState: { errors } } = useForm({
    resolver: zodResolver(schema)
  });

  const onSubmit = (data) => {
    placeOrder({ ...data, items: cartItems });
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="max-w-md mx-auto">
      <h2 className="text-2xl font-bold mb-6">Complete Your Order</h2>
      <div className="mb-4">
        <label htmlFor="name" className="block text-gray-700 mb-2">Name:</label>
        <input
          {...register("name")}
          className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        {errors.name && <p className="text-red-500 mt-1">{errors.name.message}</p>}
      </div>
      <div className="mb-4">
        <label htmlFor="address" className="block text-gray-700 mb-2">Address:</label>
        <input
          {...register("address")}
          className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        {errors.address && <p className="text-red-500 mt-1">{errors.address.message}</p>}
      </div>
      <div className="mb-4">
        <label htmlFor="phone" className="block text-gray-700 mb-2">Phone:</label>
        <input
          {...register("phone")}
          className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
        />
        {errors.phone && <p className="text-red-500 mt-1">{errors.phone.message}</p>}
      </div>
      <button
        type="submit"
        className="w-full bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition-colors"
      >
        Place Order
      </button>
    </form>
  );
};

export default OrderForm;
