import React, { useState, useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import { motion } from 'framer-motion';
import { gsap } from 'gsap';
import { Search } from 'lucide-react';

const TrackOrderPage = ({ orderHistory, getOrderStatus }) => {
  const [orderId, setOrderId] = useState('');
  const [orderStatus, setOrderStatus] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const location = useLocation();
  const formRef = useRef(null);
  const statusRef = useRef(null);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const orderIdParam = params.get('orderId');
    if (orderIdParam) {
      setOrderId(orderIdParam);
      handleTrackOrder(orderIdParam);
    }

    gsap.from(formRef.current, {
      opacity: 0,
      y: 50,
      duration: 0.8,
      ease: 'power3.out'
    });
  }, [location]);

  const handleTrackOrder = async (id = orderId) => {
    setLoading(true);
    setError(null);
    try {
      const status = getOrderStatus(id);
      if (status === 'Not Found') {
        setError('Order not found. Please check the order ID and try again.');
      } else {
        const orderDetails = orderHistory.find(order => order.id === id);
        setOrderStatus({
          status: status,
          estimatedDeliveryTime: '30 minutes',
          details: orderDetails
        });
      }
      
      if (statusRef.current) {
        gsap.from(statusRef.current, {
          opacity: 0,
          y: 30,
          duration: 0.6,
          ease: 'power2.out',
          delay: 0.2
        });
      }
    } catch (error) {
      console.error('Error fetching order status:', error);
      setError('Failed to fetch order status. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 bg-gray-100 min-h-screen">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="max-w-2xl mx-auto"
      >
        <h1 className="text-4xl font-bold mb-8 text-center text-gray-900">Track Your Order</h1>
        <div ref={formRef} className="bg-white p-6 rounded-lg shadow-lg mb-8">
          <form onSubmit={(e) => { e.preventDefault(); handleTrackOrder(); }} className="space-y-4">
            <div className="relative">
              <input
                type="text"
                value={orderId}
                onChange={(e) => setOrderId(e.target.value)}
                placeholder="Enter your order ID"
                className="w-full px-4 py-2 pr-10 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent text-gray-800 bg-white"
                style={{ backgroundColor: 'balck' }}
                required
              />
              <Search className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
            </div>
            <motion.button
              type="submit"
              className="w-full bg-green-500 text-white font-semibold py-2 px-4 rounded-md hover:bg-green-600 transition-colors duration-300 flex items-center justify-center"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              disabled={loading}
            >
              {loading ? 'Tracking...' : 'Track Order'}
            </motion.button>
          </form>
        </div>
        {loading && <p className="text-center text-gray-600">Loading order status...</p>}
        {error && <p className="text-center text-red-500 bg-white p-4 rounded-lg shadow">{error}</p>}
        {orderStatus && (
          <motion.div
            ref={statusRef}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="bg-white p-6 rounded-lg shadow-lg"
          >
            {/* Order status content */}
          </motion.div>
        )}
      </motion.div>
    </div>
  );
};

export default TrackOrderPage
