import React from 'react';
import { motion } from 'framer-motion';

const MenuItem = ({ item, addToCart }) => (
  <motion.div
    className="bg-white rounded-lg shadow-md p-4"
    whileHover={{ scale: 1.05 }}
    transition={{ type: 'spring', stiffness: 300 }}
  >
    <img src={item.image} alt={item.name} className="w-full h-48 object-cover rounded-t-lg mb-4" />
    <h3 className="text-xl font-semibold mb-2">{item.name}</h3>
    <p className="text-gray-600 mb-2">{item.description}</p>
    <p className="text-green-600 font-bold mb-4">${item.price.toFixed(2)}</p>
    <button
      onClick={() => addToCart(item)}
      className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors"
    >
      Add to Cart
    </button>
  </motion.div>
);

const Menu = ({ items, addToCart }) => {
  return (
    <div className="container mx-auto my-8">
      <h2 className="text-3xl font-bold mb-8 text-center">Our Menu</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {items.map(item => (
          <MenuItem key={item.id} item={item} addToCart={addToCart} />
        ))}
      </div>
    </div>
  );
};

export default Menu;
