import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { ShoppingCart, Home, Menu as MenuIcon, Map, User, MessageSquare } from 'lucide-react';
import SearchBar from './SearchBar';

const Header = ({ cartItemsCount, onSearch, user, onLogout, toggleChat }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <header className="bg-white shadow-lg sticky top-0 z-50">
      <div className="container mx-auto flex items-center justify-between p-4">
        {/* Logo */}
        <Link to="/" className="text-2xl font-bold text-gray-800 hover:glow-effect">
          Curry
        </Link>

        {/* Search Bar */}
        <SearchBar onSearch={onSearch} />

        {/* Icons and Links */}
        <div className="flex items-center space-x-6">
          {/* Navigation Links */}
          <nav className={`space-x-4 ${isMenuOpen ? "block" : "hidden"} md:flex`}>
            <Link to="/" className="menu-item">Home</Link>
            <Link to="/menu" className="menu-item">Menu</Link>
            <Link to="/order" className="menu-item">Order</Link>
            <Link to="/track" className="menu-item">Track</Link>
          </nav>
          {/* User Profile and Cart */}
          {user ? (
            <div className="flex items-center space-x-4">
              <Link to="/profile" className="menu-item">Profile</Link>
              <button onClick={onLogout} className="apple-button">Logout</button>
            </div>
          ) : (
            <Link to="/login" className="apple-button">Login</Link>
          )}

          {/* Cart Icon */}
          <Link to="/cart" className="relative">
            <ShoppingCart className="h-6 w-6" />
            {cartItemsCount > 0 && (
              <span className="absolute top-0 right-0 bg-red-500 text-white rounded-full h-5 w-5 flex items-center justify-center text-xs">
                {cartItemsCount}
              </span>
            )}
          </Link>

          {/* Chat Support */}
          <button onClick={toggleChat} className="apple-button flex items-center">
            <MessageSquare className="h-5 w-5 mr-1" /> Chat
          </button>

          {/* Mobile Menu Toggle */}
          <button
            className="md:hidden apple-button"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
            aria-label="Toggle menu"
          >
            <MenuIcon />
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
