import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-router-dom';
import { ShoppingBag, Trash2, Plus, Minus } from 'lucide-react';
import ErrorBoundary from './ErrorBoundary';

const CartItem = ({ item, removeFromCart, updateQuantity }) => (
  <motion.div
    className="flex justify-between items-center bg-white p-4 rounded-lg shadow mb-4"
    initial={{ opacity: 0, y: -20 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -20 }}
  >
    {/* CartItem content remains the same */}
  </motion.div>
);

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        // Replace this with your actual API call
        const response = await fetch('/api/cart');
        if (!response.ok) {
          throw new Error('Failed to fetch cart items');
        }
        const data = await response.json();
        setCartItems(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchCartItems();
  }, []);

  if (isLoading) {
    return <div>Loading cart...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <ErrorBoundary>
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-6">Your Cart</h1>
        {cartItems.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <>
            {cartItems.map((item) => (
              <CartItem
                key={item._id}
                item={item}
                removeFromCart={() => {/* implement remove logic */}}
                updateQuantity={() => {/* implement update logic */}}
              />
            ))}
            <div className="mt-6">
              <p className="text-xl font-bold">Total: â‚¹{total.toFixed(2)}</p>
              <Link
                to="/checkout"
                className="mt-4 bg-green-500 text-white px-6 py-2 rounded-lg hover:bg-green-600 transition-colors inline-block"
              >
                Proceed to Checkout
              </Link>
            </div>
          </>
        )}
      </div>
    </ErrorBoundary>
  );
};

export default Cart;
