import React, { useState, useEffect, useCallback, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { getMenuItems, submitReview } from '../services/api';
import { debounce } from 'lodash';
import { Filter, Star, ShoppingBag } from 'lucide-react';
import { Link } from 'react-router-dom';

const MenuPage = ({ addToCart, removeFromCart, updateQuantity, cartItems, initialSearchTerm = '' }) => {
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [searchTerm, setSearchTerm] = useState(initialSearchTerm);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const observer = useRef();

  const fetchMenuItems = useCallback(async (search, pageNum) => {
    try {
      setLoading(true);
      setError(null);
      const items = await getMenuItems(search, pageNum);
      setMenuItems(prevItems => [...prevItems, ...items]);
      setHasMore(items.length > 0);
    } catch (err) {
      console.error('Error fetching menu items:', err);
      setError('Failed to load menu items. Please try again.');
    } finally {
      setLoading(false);
    }
  }, []);

  const debouncedFetchMenuItems = useCallback(
    debounce((search, pageNum) => fetchMenuItems(search, pageNum), 300),
    [fetchMenuItems]
  );

  useEffect(() => {
    setMenuItems([]);
    setPage(1);
    debouncedFetchMenuItems(searchTerm, 1);
    return () => debouncedFetchMenuItems.cancel();
  }, [debouncedFetchMenuItems, searchTerm]);

  const lastItemElementRef = useCallback(node => {
    if (loading) return;
    if (observer.current) observer.current.disconnect();
    observer.current = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting && hasMore) {
        setPage(prevPage => prevPage + 1);
      }
    });
    if (node) observer.current.observe(node);
  }, [loading, hasMore]);

  useEffect(() => {
    if (page > 1) {
      debouncedFetchMenuItems(searchTerm, page);
    }
  }, [page, debouncedFetchMenuItems, searchTerm]);

  const handleSearch = (term) => {
    setSearchTerm(term);
    setMenuItems([]);
    setPage(1);
  };

  const filteredMenuItems = selectedCategory === 'All'
    ? menuItems
    : menuItems.filter(item => item.category === selectedCategory);

  const categories = ['All', ...new Set(menuItems.map(item => item.category))];

  const handleReviewSubmit = async (itemId, reviewData) => {
    try {
      await submitReview({ ...reviewData, menuItemId: itemId });
      const updatedItems = await getMenuItems(searchTerm, 1);
      setMenuItems(updatedItems);
    } catch (err) {
      console.error('Error submitting review:', err);
      setError('Failed to submit review. Please try again.');
    }
  };

  const suggestComplementaryItems = (item) => {
    const complementaryItems = menuItems.filter(menuItem => 
      (item.category === 'Main Course' && menuItem.category === 'Dessert') ||
      (item.category === 'Main Course' && menuItem.category === 'Beverage') ||
      (menuItem.name.toLowerCase().includes('water') || menuItem.name.toLowerCase().includes('soda'))
    );
    return complementaryItems.slice(0, 3);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold mb-8 text-center">Our Menu</h1>

      <SearchBar onSearch={handleSearch} initialSearchTerm={searchTerm} />

      <FilterOptions 
        categories={categories} 
        selectedCategory={selectedCategory} 
        setSelectedCategory={setSelectedCategory} 
      />

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <AnimatePresence>
          {filteredMenuItems.map((item, index) => (
            <motion.div
              key={item._id}
              ref={index === filteredMenuItems.length - 1 ? lastItemElementRef : null}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.3 }}
              className="bg-white p-6 rounded-lg shadow-md"
            >
              <h3 className="text-xl font-semibold mb-2">{item.name}</h3>
              <p className="text-gray-600 mb-4">{item.description}</p>
              <p className="text-lg font-bold mb-4">₹{item.price.toFixed(2)}</p>
              <button 
                onClick={() => addToCart(item)} 
                className="w-full bg-green-500 text-white py-2 rounded-md hover:bg-green-600 transition-colors"
              >
                Add to Cart
              </button>
              <ReviewForm onSubmit={(reviewData) => handleReviewSubmit(item._id, reviewData)} />
              <ComplementaryItems items={suggestComplementaryItems(item)} addToCart={addToCart} />
            </motion.div>
          ))}
        </AnimatePresence>
      </div>

      {loading && <div className="text-center mt-8">Loading more items...</div>}
      {error && <div className="text-center mt-8 text-red-500">{error}</div>}

      <Cart 
        cartItems={cartItems} 
        removeFromCart={removeFromCart} 
        updateQuantity={updateQuantity} 
      />
    </div>
  );
};

// ... (SearchBar, FilterOptions, ReviewForm components remain the same)

const ComplementaryItems = ({ items, addToCart }) => {
  return (
    <div className="mt-4">
      <h4 className="text-lg font-semibold mb-2">You might also like:</h4>
      <div className="flex flex-wrap gap-2">
        {items.map(item => (
          <button
            key={item._id}
            onClick={() => addToCart(item)}
            className="bg-gray-100 hover:bg-gray-200 text-gray-800 px-3 py-1 rounded-full text-sm"
          >
            {item.name} (₹{item.price.toFixed(2)})
          </button>
        ))}
      </div>
    </div>
  );
};

const Cart = ({ cartItems, removeFromCart, updateQuantity }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="fixed bottom-0 right-0 m-4">
      <button
        onClick={() => setIsExpanded(!isExpanded)}
        className="bg-green-500 text-white p-4 rounded-full shadow-lg hover:bg-green-600 transition-colors"
      >
        <ShoppingBag size={24} />
        <span className="absolute top-0 right-0 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-xs">
          {cartItems.length}
        </span>
      </button>
      
      {isExpanded && (
        <div className="absolute bottom-16 right-0 bg-white p-6 rounded-lg shadow-md w-80">
          <h2 className="text-2xl font-semibold mb-4">Your Cart</h2>
          {cartItems.length === 0 ? (
            <p>Your cart is empty.</p>
          ) : (
            <>
              {cartItems.map((item) => (
                <div key={item._id} className="flex justify-between items-center mb-4">
                  <div>
                    <h3 className="font-semibold">{item.name}</h3>
                    <p>₹{item.price.toFixed(2)} x {item.quantity}</p>
                  </div>
                  <div className="flex items-center">
                    <input
                      type="number"
                      min="1"
                      value={item.quantity}
                      onChange={(e) => updateQuantity(item._id, parseInt(e.target.value))}
                      className="w-16 px-2 py-1 border rounded mr-2"
                    />
                    <button
                      onClick={() => removeFromCart(item._id)}
                      className="text-red-500 hover:text-red-700"
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
              <div className="mt-4 pt-4 border-t border-gray-200">
                <p className="text-xl font-bold">Total: ₹{total.toFixed(2)}</p>
                <Link
                  to="/checkout"
                  className="mt-4 bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition-colors flex items-center justify-center"
                >
                  <ShoppingBag className="mr-2" size={20} />
                  Proceed to Checkout
                </Link>
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default MenuPage;
