import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { ShoppingCart, Home, Menu as MenuIcon, Map, User, LogOut } from 'lucide-react';
import { motion } from 'framer-motion';
import { useCartContext } from '../context/CartContext';
import { useAuthContext } from '../context/AuthContext';
import SearchBar from './SearchBar';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const { cartItems } = useCartContext();
  const { user, logout } = useAuthContext();

  const navItems = [
    { to: '/', icon: Home, label: 'Home' },
    { to: '/menu', icon: MenuIcon, label: 'Menu' },
    { to: '/track', icon: Map, label: 'Track' },
    { to: '/profile', icon: User, label: 'Profile' },
  ];

  return (
    <header className="bg-green-600 text-white p-4 sticky top-0 z-50">
      <div className="container mx-auto flex flex-wrap justify-between items-center">
        <Link to="/" className="text-2xl font-bold">Veg-App</Link>
        
        <button 
          className="lg:hidden"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          <MenuIcon size={24} />
        </button>

        <nav className={`${isMenuOpen ? 'block' : 'hidden'} lg:block w-full lg:w-auto mt-4 lg:mt-0`}>
          <ul className="flex flex-col lg:flex-row space-y-2 lg:space-y-0 lg:space-x-4">
            {navItems.map((item) => (
              <li key={item.to}>
                <Link to={item.to} className="flex items-center hover:text-green-200">
                  <item.icon className="mr-1" size={18} />
                  {item.label}
                </Link>
              </li>
            ))}
            {user && (
              <li>
                <button onClick={logout} className="flex items-center hover:text-green-200">
                  <LogOut className="mr-1" size={18} />
                  Logout
                </button>
              </li>
            )}
          </ul>
        </nav>

        <div className="w-full lg:w-auto mt-4 lg:mt-0">
          <SearchBar />
        </div>

        <Link to="/cart" className="flex items-center mt-4 lg:mt-0">
          <motion.div whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
            <ShoppingCart className="mr-1" size={18} />
            <span className="bg-white text-green-600 rounded-full px-2 py-1 text-xs font-bold">
              {cartItems.length}
            </span>
          </motion.div>
        </Link>
      </div>
    </header>
  );
};

export default Header;
