import React from 'react';
import { motion } from 'framer-motion';
import { Truck, Clock, CheckCircle } from 'lucide-react';

const DeliveryStatus = ({ status, estimatedDeliveryTime, deliveryOTP }) => {
  const getStatusIcon = () => {
    switch (status) {
      case 'Preparing':
        return <Clock className="text-yellow-500" size={24} />;
      case 'Out for Delivery':
        return <Truck className="text-blue-500" size={24} />;
      case 'Delivered':
        return <CheckCircle className="text-green-500" size={24} />;
      default:
        return null;
    }
  };

  return (
    <motion.div
      className="bg-white p-6 rounded-lg shadow-lg"
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <h2 className="text-2xl font-bold mb-4 flex items-center">
        {getStatusIcon()}
        <span className="ml-2">Delivery Status</span>
      </h2>
      <p className="text-lg mb-2">Current Status: <span className="font-semibold">{status}</span></p>
      <p className="text-lg mb-4">Estimated Delivery Time: <span className="font-semibold">{estimatedDeliveryTime}</span></p>
      {status === 'Out for Delivery' && (
        <motion.div
          className="bg-green-100 border-l-4 border-green-500 p-4 mt-4"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.3 }}
        >
          <p className="font-bold">Delivery OTP: <span className="text-green-700">{deliveryOTP}</span></p>
          <p className="mt-2 text-sm text-gray-600">Please share this OTP with the delivery agent upon arrival.</p>
        </motion.div>
      )}
    </motion.div>
  );
};

export default DeliveryStatus;
