import React, { useState, useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import { motion } from 'framer-motion';
import { gsap } from 'gsap';
import { Package, Truck, Home, CheckCircle, Search } from 'lucide-react';

const DeliveryStatus = ({ status, estimatedDeliveryTime }) => {
  const statusSteps = [
    { icon: Package, label: 'Order Placed' },
    { icon: Truck, label: 'Out for Delivery' },
    { icon: Home, label: 'Delivered' }
  ];

  const currentStep = status === 'Delivered' ? 2 : status === 'Out for Delivery' ? 1 : 0;

  return (
    <div className="mb-8">
      <div className="flex justify-between mb-4">
        {statusSteps.map((step, index) => (
          <div key={index} className="flex flex-col items-center">
            <div className={`rounded-full p-3 ${index <= currentStep ? 'bg-green-500' : 'bg-gray-300'}`}>
              <step.icon className="w-6 h-6 text-white" />
            </div>
            <span className="mt-2 text-sm">{step.label}</span>
          </div>
        ))}
      </div>
      <div className="h-2 bg-gray-200 rounded-full">
        <div 
          className="h-full bg-green-500 rounded-full transition-all duration-500 ease-out"
          style={{ width: `${(currentStep / 2) * 100}%` }}
        />
      </div>
      {estimatedDeliveryTime && (
        <p className="mt-4 text-center">
          Estimated delivery time: {estimatedDeliveryTime}
        </p>
      )}
    </div>
  );
};

const TrackOrderPage = ({ orderHistory, getOrderStatus }) => {
  const [orderId, setOrderId] = useState('');
  const [orderStatus, setOrderStatus] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const location = useLocation();
  const formRef = useRef(null);
  const statusRef = useRef(null);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const orderIdParam = params.get('orderId');
    if (orderIdParam) {
      setOrderId(orderIdParam);
      handleTrackOrder(orderIdParam);
    }

    // GSAP animation
    gsap.from(formRef.current, {
      opacity: 0,
      y: 50,
      duration: 0.8,
      ease: 'power3.out'
    });
  }, [location]);

  const handleTrackOrder = async (id = orderId) => {
    setLoading(true);
    setError(null);
    try {
      // Use the getOrderStatus function passed as a prop
      const status = getOrderStatus(id);
      if (status === 'Not Found') {
        setError('Order not found. Please check the order ID and try again.');
      } else {
        const orderDetails = orderHistory.find(order => order.id === id);
        setOrderStatus({
          status: status,
          estimatedDeliveryTime: '30 minutes', // You can calculate this based on order time if available
          details: orderDetails
        });
      }
      
      // Animate status appearance
      if (statusRef.current) {
        gsap.from(statusRef.current, {
          opacity: 0,
          y: 30,
          duration: 0.6,
          ease: 'power2.out',
          delay: 0.2
        });
      }
    } catch (error) {
      console.error('Error fetching order status:', error);
      setError('Failed to fetch order status. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-4xl font-bold mb-8 text-center text-gray-800">Track Your Order</h1>
        <div ref={formRef} className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-lg mb-8">
          <form onSubmit={(e) => { e.preventDefault(); handleTrackOrder(); }} className="space-y-4">
            <input
              type="text"
              value={orderId}
              onChange={(e) => setOrderId(e.target.value)}
              placeholder="Enter your order ID"
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              required
            />
            <motion.button
              type="submit"
              className="w-full bg-green-500 text-white py-2 rounded-md hover:bg-green-600 transition-colors duration-300 flex items-center justify-center"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              disabled={loading}
            >
              {loading ? 'Tracking...' : (
                <>
                  <Search className="w-5 h-5 mr-2" />
                  Track Order
                </>
              )}
            </motion.button>
          </form>
        </div>
        {loading && <p className="text-center">Loading order status...</p>}
        {error && <p className="text-center text-red-500">{error}</p>}
        {orderStatus && (
          <motion.div
            ref={statusRef}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <DeliveryStatus
              status={orderStatus.status}
              estimatedDeliveryTime={orderStatus.estimatedDeliveryTime}
            />
            {orderStatus.status === 'Delivered' && (
              <div className="text-center">
                <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-4" />
                <p className="text-xl font-semibold">Your order has been delivered!</p>
                <p className="mt-2">Thank you for choosing our service.</p>
              </div>
            )}
            {orderStatus.details && (
              <div className="mt-8 bg-white p-6 rounded-lg shadow-lg">
                <h2 className="text-2xl font-bold mb-4">Order Details</h2>
                <p><strong>Order ID:</strong> {orderStatus.details.id}</p>
                <p><strong>Date:</strong> {new Date(orderStatus.details.date).toLocaleString()}</p>
                <p><strong>Total:</strong> ₹{orderStatus.details.total.toFixed(2)}</p>
                <h3 className="text-xl font-semibold mt-4 mb-2">Items:</h3>
                <ul className="list-disc list-inside">
                  {orderStatus.details.items.map((item, index) => (
                    <li key={index}>{item.name} x {item.quantity}</li>
                  ))}
                </ul>
              </div>
            )}
          </motion.div>
        )}
      </motion.div>
    </div>
  );
};

export default TrackOrderPage;
