import React, { useState, useEffect, useCallback, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { getMenuItems, submitReview } from '../services/api';
import { debounce } from 'lodash';
import { Filter, Star, ShoppingBag } from 'lucide-react';
import { Link } from 'react-router-dom';

const MenuPage = ({ addToCart, removeFromCart, updateQuantity, cartItems, initialSearchTerm = '' }) => {
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [searchTerm, setSearchTerm] = useState(initialSearchTerm);
  const [page, setPage] = useState(1); // Updated state for pagination
  const [hasMore, setHasMore] = useState(true); // For infinite scrolling
  const observer = useRef();

  const fetchMenuItems = useCallback(async (search, pageNum = 1) => {
    try {
      setLoading(true);
      setError(null);
      const items = await getMenuItems(search, pageNum); // API now takes page number
      setMenuItems(prevItems => pageNum === 1 ? items : [...prevItems, ...items]); // Append if not first page
      setHasMore(items.length > 0); // Check if more items exist
    } catch (err) {
      console.error('Error fetching menu items:', err);
      setError('Failed to load menu items. Please try again.');
    } finally {
      setLoading(false);
    }
  }, []);

  const debouncedFetchMenuItems = useCallback(
    debounce((search, pageNum) => fetchMenuItems(search, pageNum), 300),
    [fetchMenuItems]
  );

  useEffect(() => {
    setMenuItems([]); // Clear items on search/filter change
    debouncedFetchMenuItems(searchTerm, 1); // Fetch new items from page 1
    return () => debouncedFetchMenuItems.cancel();
  }, [debouncedFetchMenuItems, searchTerm, selectedCategory]);

  const handleSearch = (term) => {
    setSearchTerm(term);
    setPage(1); // Reset page on new search
  };

  // Filter and pagination logic
  const filteredMenuItems = selectedCategory === 'All'
    ? menuItems
    : menuItems.filter(item => item.category === selectedCategory);

  const lastItemRef = useCallback(node => {
    if (loading) return;
    if (observer.current) observer.current.disconnect();

    observer.current = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting && hasMore) {
        setPage(prevPage => prevPage + 1);
      }
    });

    if (node) observer.current.observe(node);
  }, [loading, hasMore]);

  useEffect(() => {
    if (page > 1) {
      debouncedFetchMenuItems(searchTerm, page); // Fetch more items on new page
    }
  }, [page, searchTerm, debouncedFetchMenuItems]);

  const categories = ['All', ...new Set(menuItems.map(item => item.category))];

  const handleReviewSubmit = async (itemId, reviewData) => {
    try {
      await submitReview({ ...reviewData, menuItemId: itemId });
      await fetchMenuItems(searchTerm, 1); // Re-fetch items to reflect review changes
    } catch (err) {
      console.error('Error submitting review:', err);
      setError('Failed to submit review. Please try again.');
    }
  };

  if (loading && page === 1) {
    return <div className="text-center mt-8">Loading menu items...</div>;
  }

  if (error) {
    return <div className="text-center mt-8 text-red-500">{error}</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold mb-8 text-center">Our Menu</h1>

      <SearchBar onSearch={handleSearch} initialSearchTerm={searchTerm} />

      <FilterOptions 
        categories={categories} 
        selectedCategory={selectedCategory} 
        setSelectedCategory={setSelectedCategory} 
      />

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <AnimatePresence>
          {filteredMenuItems.map((item, index) => {
            const isLastItem = filteredMenuItems.length === index + 1;
            return (
              <motion.div
                key={item._id}
                ref={isLastItem ? lastItemRef : null} // Attach observer to last item
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 }}
                className="bg-white p-6 rounded-lg shadow-md"
              >
                <h3 className="text-xl font-semibold mb-2">{item.name}</h3>
                <p className="text-gray-600 mb-4">{item.description}</p>
                <p className="text-lg font-bold mb-4">â‚¹{item.price.toFixed(2)}</p>
                <button 
                  onClick={() => addToCart(item)} 
                  className="w-full bg-green-500 text-white py-2 rounded-md hover:bg-green-600 transition-colors"
                >
                  Add to Cart
                </button>
                <ReviewForm onSubmit={(reviewData) => handleReviewSubmit(item._id, reviewData)} />
              </motion.div>
            );
          })}
        </AnimatePresence>
      </div>

      <Cart 
        cartItems={cartItems} 
        removeFromCart={removeFromCart} 
        updateQuantity={updateQuantity} 
      />
    </div>
  );
};

export default MenuPage;
