import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

const MenuPage = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulating API call to get menu items
    const fetchMenuItems = async () => {
      // Replace this with actual API call in production
      const items = [
        { id: 1, name: 'Idli (4 pieces)', description: 'Soft steamed rice cakes', price: 80, category: 'Breakfast' },
        { id: 2, name: 'Masala Dosa', description: 'Crispy dosa with spiced potato filling', price: 120, category: 'Breakfast' },
        { id: 3, name: 'Paneer Butter Masala', description: 'Paneer cubes in rich tomato gravy', price: 140, category: 'Main Course' },
        { id: 4, name: 'Veg Biryani', description: 'Fragrant rice dish with mixed vegetables', price: 150, category: 'Main Course' },
        { id: 5, name: 'Gulab Jamun', description: 'Sweet milk-solid-based balls', price: 60, category: 'Desserts' },
        // Add more menu items here
      ];
      setMenuItems(items);
      setLoading(false);
    };
    fetchMenuItems();
  }, []);

  useEffect(() => {
    // GSAP animations for menu items
    menuItems.forEach((_, index) => {
      gsap.from(`#menu-item-${index}`, {
        duration: 1,
        opacity: 0,
        y: 50,
        ease: "power3.out",
        scrollTrigger: {
          trigger: `#menu-item-${index}`,
          start: "top 80%",
        }
      });
    });
  }, [menuItems]);

  const addToCart = (item) => {
    setCart([...cart, item]);
    
    // Animation for adding item to cart
    gsap.from("#cart div:last-child", {
      opacity: 0,
      y: -20,
      duration: 0.5,
      ease: "back.out(1.7)"
    });
  };

  const removeFromCart = (itemId) => {
    setCart(cart.filter(item => item.id !== itemId));
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + item.price, 0);
  };

  const groupedMenuItems = menuItems.reduce((acc, item) => {
    if (!acc[item.category]) {
      acc[item.category] = [];
    }
    acc[item.category].push(item);
    return acc;
  }, {});

  if (loading) {
    return <div className="text-center mt-8">Loading menu items...</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-4xl font-bold mb-8 text-center">Our Menu</h1>
        <div className="flex flex-col lg:flex-row gap-8">
          <div className="lg:w-2/3">
            {Object.entries(groupedMenuItems).map(([category, items]) => (
              <div key={category} className="mb-8">
                <h2 className="text-2xl font-semibold mb-4">{category}</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {items.map((item, index) => (
                    <motion.div
                      key={item.id}
                      id={`menu-item-${index}`}
                      className="bg-white p-4 rounded-lg shadow"
                      whileHover={{ scale: 1.05 }}
                    >
                      <h3 className="text-xl font-semibold">{item.name}</h3>
                      <p className="text-gray-600">{item.description}</p>
                      <div className="flex justify-between items-center mt-2">
                        <span className="text-lg font-bold">₹{item.price}</span>
                        <motion.button
                          onClick={() => addToCart(item)}
                          className="bg-green-500 text-white px-4 py-2 rounded-full hover:bg-green-600"
                          whileHover={{ scale: 1.1 }}
                          whileTap={{ scale: 0.9 }}
                        >
                          Add to Cart
                        </motion.button>
                      </div>
                    </motion.div>
                  ))}
                </div>
              </div>
            ))}
          </div>
          <div className="lg:w-1/3">
            <div className="bg-white p-6 rounded-lg shadow sticky top-4">
              <h2 className="text-2xl font-semibold mb-4">Your Cart</h2>
              {cart.length === 0 ? (
                <p>Your cart is empty</p>
              ) : (
                <>
                  {cart.map((item) => (
                    <div key={item.id} className="flex justify-between items-center mb-2">
                      <span>{item.name}</span>
                      <div>
                        <span className="mr-2">₹{item.price}</span>
                        <button
                          onClick={() => removeFromCart(item.id)}
                          className="text-red-500 hover:text-red-700"
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  ))}
                  <div className="border-t border-gray-200 mt-4 pt-4">
                    <div className="flex justify-between font-bold">
                      <span>Total</span>
                      <span>₹{getTotalPrice()}</span>
                    </div>
                  </div>
                  <motion.button
                    className="w-full bg-green-500 text-white py-2 rounded-full mt-4 hover:bg-green-600"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    Checkout
                  </motion.button>
                </>
              )}
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default MenuPage;
