import React, { useState, useEffect } from 'react';
import Menu from '../components/Menu';
import Cart from '../components/Cart';
import { getMenuItems } from '../services/api';
import { motion } from 'framer-motion';

const MenuPage = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMenuItems = async () => {
      try {
        const items = await getMenuItems();
        setMenuItems(items);
      } catch (error) {
        console.error('Error fetching menu items:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchMenuItems();
  }, []);

  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const removeFromCart = (itemId) => {
    setCartItems(cartItems.filter(item => item.id !== itemId));
  };

  const clearCart = () => {
    setCartItems([]);
  };

  if (loading) {
    return <div className="text-center mt-8">Loading menu items...</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-4xl font-bold mb-8 text-center">Our Menu</h1>
        <div className="flex flex-col lg:flex-row gap-8">
          <div className="lg:w-2/3">
            <Menu items={menuItems} addToCart={addToCart} />
          </div>
          <div className="lg:w-1/3">
            <Cart items={cartItems} removeFromCart={removeFromCart} clearCart={clearCart} />
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default MenuPage;
