import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import MenuPage from './pages/MenuPage';
import OrderPage from './pages/OrderPage';
import TrackOrderPage from './pages/TrackOrderPage';
import { gsap } from 'gsap';

const App = () => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // GSAP animation for page transitions
    gsap.to('body', { opacity: 1, duration: 0.5, ease: 'power2.out' });
  }, []);

  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  const removeFromCart = (itemId) => {
    setCartItems(cartItems.filter(item => item.id !== itemId));
  };

  const clearCart = () => {
    setCartItems([]);
  };

  return (
    <Router>
      <div className="app bg-gradient-to-br from-gray-100 to-gray-200 min-h-screen">
        <Header />
        <main className="pb-16">
          <Switch>
            <Route exact path="/" component={Home} />
            <Route path="/menu">
              <MenuPage addToCart={addToCart} removeFromCart={removeFromCart} clearCart={clearCart} cartItems={cartItems} />
            </Route>
            <Route path="/order">
              <OrderPage cartItems={cartItems} />
            </Route>
            <Route path="/track" component={TrackOrderPage} />
          </Switch>
        </main>
        <Footer />
      </div>
    </Router>
  );
};

export default App;
