        { id: 34, name: 'Rasam', description: 'Tangy and spicy soup', price: 50, category: 'Sides', rating: 4.3 },
        { id: 35, name: 'Tomato-Radish Chutney', description: 'Tangy chutney', price: 30, category: 'Chutneys & Sides' },
        { id: 36, name: 'Peanut Chutney', description: 'Nutty peanut chutney', price: 30, category: 'Chutneys & Sides' },
        { id: 37, name: 'Ginger Chutney', description: 'Spicy ginger chutney', price: 30, category: 'Chutneys & Sides' },
      
        // Beverages
        { id: 38, name: 'Filter Coffee', description: 'Strong and aromatic South Indian coffee', price: 40, category: 'Beverages', rating: 4.9 },
        { id: 39, name: 'Masala Chai', description: 'Spiced Indian tea', price: 30, category: 'Beverages', rating: 4.6 },
      
        // Desserts
        { id: 40, name: 'Gulab Jamun', description: 'Deep-fried milk solids soaked in sugar syrup', price: 70, category: 'Desserts', rating: 4.7 },
        { id: 41, name: 'Mysore Pak', description: 'Traditional ghee-based sweet', price: 60, category: 'Desserts', rating: 4.5 },
        { id: 42, name: 'Choco Lava Cake', description: 'Chocolate cake with molten center', price: 100, category: 'Desserts' },
        { id: 43, name: 'Choco Chip Ice Cream', description: 'Vanilla ice cream with chocolate chips', price: 80, category: 'Desserts' },
        { id: 44, name: 'Kaju Katli', description: 'Cashew-based sweet', price: 70, category: 'Desserts' },
      
        // Snacks
        { id: 45, name: 'Samosa', description: 'Fried pastry with savory filling', price: 30, category: 'Snacks' },
        { id: 46, name: 'Popcorn', description: 'Freshly popped corn', price: 50, category: 'Snacks' },
        { id: 47, name: 'Pav Bhaji', description: 'Spicy vegetable curry with bread rolls', price: 100, category: 'Snacks', rating: 4.4 },
        { id: 48, name: 'Mirchi Bhaji', description: 'Batter-fried green chilies', price: 70, category: 'Snacks' },
      ];
    }
        setMenuItems(items);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching menu items:', error);
        setError('Failed to load menu items. Please try again later.');
        setLoading(false);
      }
    };
    fetchMenuItems();
  }, [searchTerm]);

  useEffect(() => {
    menuItems.forEach((_, index) => {
      gsap.from(`#menu-item-${index}`, {
        duration: 1,
        opacity: 0,
        y: 50,
        ease: "power3.out",
        scrollTrigger: {
          trigger: `#menu-item-${index}`,
          start: "top 80%",
        }
      });
    });
  }, [menuItems]);

  const categories = ['All', ...new Set(menuItems.map(item => item.category))];

  const filteredMenuItems = selectedCategory === 'All'
    ? menuItems
    : menuItems.filter(item => item.category === selectedCategory);

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleReviewSubmit = async (itemId, reviewData) => {
    try {
      await submitReview({ ...reviewData, menuItemId: itemId });
      // Optionally refresh the menu items or update local state to reflect new review
    } catch (error) {
      console.error('Error submitting review:', error);
      // Handle error (e.g., show an error message to the user)
    }
  };

  if (loading) {
    return <div className="text-center mt-8">Loading menu items...</div>;
  }

  if (error) {
    return <div className="text-center mt-8 text-red-500">{error}</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-4xl font-bold mb-8 text-center">Our Menu</h1>

        <FilterOptions
          categories={categories}
          selectedCategory={selectedCategory}
          setSelectedCategory={setSelectedCategory}
        />

        <div className="flex flex-col lg:flex-row gap-8">
          <div className="lg:w-2/3">
            {Object.entries(filteredMenuItems.reduce((acc, item) => {
              if (!acc[item.category]) {
                acc[item.category] = [];
              }
              acc[item.category].push(item);
              return acc;
            }, {})).map(([category, items]) => (
              <div key={category} className="mb-8">
                <h2 className="text-2xl font-semibold mb-4">{category}</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {items.map((item, index) => (
                    <motion.div
                      key={item.id}
                      id={`menu-item-${index}`}
                      className="bg-white p-4 rounded-lg shadow"
                      whileHover={{ scale: 1.05 }}
                    >
                      <h3 className="text-xl font-semibold">{item.name}</h3>
                      <p>{item.description}</p>
                      <p>Price: ₹{item.price}</p>
                      <button
                        onClick={() => addToCart(item)}
                        className="bg-green-500 text-white px-3 py-1 mt-2 rounded"
                      >
                        Add to Cart
                      </button>
                      <ReviewForm
                        onSubmit={(reviewData) => handleReviewSubmit(item.id, reviewData)}
                      />
                    </motion.div>
                  ))}
                </div>
              </div>
            ))}
          </div>

          <div className="lg:w-1/3">
            <h2 className="text-2xl font-semibold mb-4">Cart</h2>
            <div id="cart" className="space-y-4">
              {cartItems.length === 0 ? (
                <p>Your cart is empty.</p>
              ) : (
                cartItems.map((item, index) => (
                  <div key={index} className="bg-gray-100 p-4 rounded-lg">
                    <p>{item.name} - ₹{item.price} x {item.quantity}</p>
                    <button
                      onClick={() => removeFromCart(item.id)}
                      className="text-red-500"
                    >
                      Remove
                    </button>
                    <input
                      type="number"
                      min="1"
                      max="6"
                      value={item.quantity}
                      onChange={(e) => updateQuantity(item.id, parseInt(e.target.value))}
                      className="ml-2 w-16 px-2 py-1 border rounded"
                    />
                  </div>
                ))
              )}
            </div>
            <div className="mt-4 pt-4 border-t border-gray-300">
              <p className="text-xl font-bold">Total: ₹{total.toFixed(2)}</p>
              <div className="mt-4 flex justify-between">
                <Link
                  to="/checkout"
                  className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors flex items-center"
                >
                  <ShoppingBag className="mr-2" size={20} />
                  Proceed to Checkout
                </Link>
              </div>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default MenuPage;
