import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import gsap from 'gsap';
import { TextPlugin } from 'gsap/TextPlugin';

gsap.registerPlugin(TextPlugin);

const Home = () => {
  const logoRef = useRef(null);

  useEffect(() => {
    // Logo animation
    gsap.to(logoRef.current, {
      duration: 2,
      text: {
        value: "Curry",
        delimiter: "",
        oldClass: "start",
        newClass: "end"
      },
      repeat: -1,
      yoyo: true,
      ease: "power1.inOut",
    });
  }, []);

  return (
    <div className="bg-green-50 min-h-screen">
     

      <div className="flex items-center justify-center h-[calc(100vh-64px)]">
        <motion.div
          className="text-center"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          <h1 className="text-5xl font-bold mb-4 text-green-800">Welcome to Curry</h1>
          <p className="text-xl mb-8 text-gray-600">Delicious vegetarian cuisine at your doorstep</p>
          <Link to="/menu">
            <motion.button
              className="bg-green-500 text-white px-8 py-3 rounded-full text-lg font-semibold hover:bg-green-600 transition-colors"
              whileHover={{ scale: 1.05, boxShadow: '0 0 10px rgba(76,175,80,0.7)' }}
              whileTap={{ scale: 0.95 }}
            >
              View Menu
            </motion.button>
          </Link>
        </motion.div>
      </div>
    </div>
  );
};

export default Home;
