import React, { useState, useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import DeliveryStatus from '../components/DeliveryStatus';
import { getOrderStatus } from '../services/api';
import { gsap } from 'gsap';

const TrackOrderPage = () => {
  const [orderId, setOrderId] = useState('');
  const [orderStatus, setOrderStatus] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const location = useLocation();
  const formRef = useRef(null);
  const statusRef = useRef(null);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const orderIdParam = params.get('orderId');
    if (orderIdParam) {
      setOrderId(orderIdParam);
      handleTrackOrder(orderIdParam);
    }

    // GSAP animation
    gsap.from(formRef.current, {
      opacity: 0,
      y: 50,
      duration: 0.8,
      ease: 'power3.out'
    });
  }, [location]);

  const handleTrackOrder = async (id = orderId) => {
    setLoading(true);
    setError(null);
    try {
      const status = await getOrderStatus(id);
      setOrderStatus(status);
      
      // Animate status appearance
      if (statusRef.current) {
        gsap.from(statusRef.current, {
          opacity: 0,
          y: 30,
          duration: 0.6,
          ease: 'power2.out',
          delay: 0.2
        });
      }
    } catch (error) {
      console.error('Error fetching order status:', error);
      setError('Failed to fetch order status. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold mb-8 text-center text-gray-800">Track Your Order</h1>
      <div ref={formRef} className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-lg mb-8">
        <form onSubmit={(e) => { e.preventDefault(); handleTrackOrder(); }} className="space-y-4">
          <input
            type="text"
            value={orderId}
            onChange={(e) => setOrderId(e.target.value)}
            placeholder="Enter your order ID"
            className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600 transition-colors duration-300"
          >
            Track Order
          </button>
        </form>
      </div>
      {loading && <p className="text-center">Loading order status...</p>}
      {error && <p className="text-center text-red-500">{error}</p>}
      {orderStatus && (
        <div ref={statusRef}>
          <DeliveryStatus
            status={orderStatus.status}
            estimatedDeliveryTime={orderStatus.estimatedDeliveryTime}
            deliveryOTP={orderStatus.deliveryOTP}
          />
        </div>
      )}
    </div>
  );
};

export default TrackOrderPage;
