import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

const Cart = ({ cartItems, removeFromCart }) => {
  const getTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.price, 0);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-4xl font-bold mb-8 text-center text-gray-800">Your Cart</h1>
        {cartItems.length === 0 ? (
          <p className="text-center text-lg text-gray-600">Your cart is empty</p>
        ) : (
          <div className="max-w-2xl mx-auto">
            {cartItems.map((item) => (
              <div key={item.id} className="flex justify-between items-center mb-4 p-4 bg-white rounded-lg shadow">
                <div>
                  <h3 className="text-lg font-semibold">{item.name}</h3>
                  <p className="text-gray-600">₹{item.price}</p>
                </div>
                <button
                  onClick={() => removeFromCart(item.id)}
                  className="text-red-500 hover:text-red-700"
                >
                  Remove
                </button>
              </div>
            ))}
            <div className="mt-8">
              <div className="flex justify-between items-center mb-4">
                <span className="text-xl font-bold">Total:</span>
                <span className="text-xl font-bold">₹{getTotalPrice()}</span>
              </div>
              <Link to="/order">
                <motion.button
                  className="w-full bg-green-500 text-white py-3 rounded-full text-lg font-semibold hover:bg-green-600 transition-colors"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Proceed to Checkout
                </motion.button>
              </Link>
            </div>
          </div>
        )}
      </motion.div>
    </div>
  );
};

export default Cart;
