import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

const MenuPage = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulating API call to get menu items
    const fetchMenuItems = async () => {
      const items = [
        // Breakfast
        { id: 1, name: 'Idli (4 pieces)', description: 'Soft steamed rice cakes', price: 80, category: 'Breakfast' },
        { id: 2, name: 'Masala Dosa', description: 'Crispy dosa with spiced potato filling', price: 120, category: 'Breakfast' },
        { id: 3, name: 'Onion Dosa', description: 'Crispy dosa with onion topping', price: 100, category: 'Breakfast' },
        { id: 4, name: 'Plain Dosa', description: 'Classic crispy dosa', price: 90, category: 'Breakfast' },
        { id: 5, name: 'Vada', description: 'Crispy lentil fritters', price: 60, category: 'Breakfast' },
        { id: 6, name: 'Upma', description: 'Savory semolina porridge', price: 70, category: 'Breakfast' },
        { id: 7, name: 'Vermicelli Upma', description: 'Savory vermicelli dish', price: 75, category: 'Breakfast' },
        { id: 8, name: 'Poha', description: 'Flattened rice dish', price: 65, category: 'Breakfast' },
        { id: 9, name: 'Parota', description: 'Layered flatbread', price: 40, category: 'Breakfast' },
        { id: 10, name: 'Chole Bhature', description: 'Spicy chickpea curry with fried bread', price: 110, category: 'Breakfast' },
        { id: 11, name: 'Aloo Curry', description: 'Potato curry', price: 80, category: 'Breakfast' },
        { id: 12, name: 'Oats Porridge', description: 'With nuts and fruits', price: 90, category: 'Breakfast' },
        { id: 13, name: 'Pancakes', description: 'With honey or maple syrup', price: 100, category: 'Breakfast' },

        // Chutneys & Sides
        { id: 14, name: 'Tomato-Radish Chutney', description: 'Tangy chutney', price: 30, category: 'Chutneys & Sides' },
        { id: 15, name: 'Coconut Chutney', description: 'Classic coconut chutney', price: 30, category: 'Chutneys & Sides' },
        { id: 16, name: 'Peanut Chutney', description: 'Nutty peanut chutney', price: 30, category: 'Chutneys & Sides' },
        { id: 17, name: 'Ginger Chutney', description: 'Spicy ginger chutney', price: 30, category: 'Chutneys & Sides' },
        { id: 18, name: 'Sambar', description: 'Lentil-based vegetable stew', price: 40, category: 'Chutneys & Sides' },

        // Veg Meals
        { id: 19, name: 'Tomato Rice', description: 'Rice cooked with tomatoes', price: 90, category: 'Veg Meals' },
        { id: 20, name: 'Jeera Rice', description: 'Cumin-flavored rice', price: 80, category: 'Veg Meals' },
        { id: 21, name: 'Pudina Rice', description: 'Mint-flavored rice', price: 85, category: 'Veg Meals' },
        { id: 22, name: 'Lemon Rice', description: 'Tangy lemon-flavored rice', price: 80, category: 'Veg Meals' },
        { id: 23, name: 'Tamarind Rice', description: 'Tangy tamarind-flavored rice', price: 85, category: 'Veg Meals' },
        { id: 24, name: 'Curd Rice', description: 'Rice mixed with yogurt', price: 70, category: 'Veg Meals' },
        { id: 25, name: 'Veg Curry and Rice', description: 'Mixed vegetable curry with rice', price: 100, category: 'Veg Meals' },
        { id: 26, name: 'Dal and Rice', description: 'Lentil curry with rice', price: 90, category: 'Veg Meals' },
        { id: 27, name: 'Paneer Rice', description: 'Rice with cottage cheese', price: 110, category: 'Veg Meals' },

        // Main Course
        { id: 28, name: 'Paneer Butter Masala', description: 'Paneer cubes in rich tomato gravy', price: 140, category: 'Main Course' },
        { id: 29, name: 'Butter Naan', description: 'Buttered flatbread', price: 40, category: 'Main Course' },
        { id: 30, name: 'Veg Fried Rice', description: 'Rice stir-fried with vegetables', price: 120, category: 'Main Course' },
        { id: 31, name: 'Veg Biryani', description: 'Fragrant rice dish with mixed vegetables', price: 150, category: 'Main Course' },
        { id: 32, name: 'Veg Pizza', description: 'Pizza topped with vegetables', price: 200, category: 'Main Course' },
        { id: 33, name: 'Pasta', description: 'Italian pasta dish', price: 180, category: 'Main Course' },
        { id: 34, name: 'Mexican Bowl', description: 'Mixed Mexican-style vegetables', price: 220, category: 'Main Course' },
        { id: 35, name: 'Spicy Ramen', description: 'Japanese-style noodle soup', price: 180, category: 'Main Course' },
        { id: 36, name: 'Veg Sushi', description: 'Japanese rice rolls with vegetables', price: 250, category: 'Main Course' },

        // Desserts
        { id: 37, name: 'Gulab Jamun', description: 'Sweet milk-solid-based balls', price: 60, category: 'Desserts' },
        { id: 38, name: 'Choco Lava Cake', description: 'Chocolate cake with molten center', price: 100, category: 'Desserts' },
        { id: 39, name: 'Choco Chip Ice Cream', description: 'Vanilla ice cream with chocolate chips', price: 80, category: 'Desserts' },
        { id: 40, name: 'Kaju Katli', description: 'Cashew-based sweet', price: 70, category: 'Desserts' },

        // Snacks
        { id: 41, name: 'Samosa', description: 'Fried pastry with savory filling', price: 30, category: 'Snacks' },
        { id: 42, name: 'Popcorn', description: 'Freshly popped corn', price: 50, category: 'Snacks' },
        { id: 43, name: 'Pav Bhaji', description: 'Spicy vegetable curry with bread rolls', price: 100, category: 'Snacks' },
        { id: 44, name: 'Mirchi Bhaji', description: 'Batter-fried green chilies', price: 70, category: 'Snacks' },
      ];
      setMenuItems(items);
      setLoading(false);
    };
    fetchMenuItems();
  }, []);

  useEffect(() => {
    // GSAP animations for menu items
    menuItems.forEach((_, index) => {
      gsap.from(`#menu-item-${index}`, {
        duration: 1,
        opacity: 0,
        y: 50,
        ease: "power3.out",
        scrollTrigger: {
          trigger: `#menu-item-${index}`,
          start: "top 80%",
        }
      });
    });
  }, [menuItems]);

  const addToCart = (item) => {
    setCart([...cart, item]);
    
    // Animation for adding item to cart
    gsap.from("#cart div:last-child", {
      opacity: 0,
      y: -20,
      duration: 0.5,
      ease: "back.out(1.7)"
    });
  };

  const removeFromCart = (itemId) => {
    setCart(cart.filter(item => item.id !== itemId));
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + item.price, 0);
  };

  const groupedMenuItems = menuItems.reduce((acc, item) => {
    if (!acc[item.category]) {
      acc[item.category] = [];
    }
    acc[item.category].push(item);
    return acc;
  }, {});

  if (loading) {
    return <div className="text-center mt-8">Loading menu items...</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-4xl font-bold mb-8 text-center">Our Menu</h1>
        <div className="flex flex-col lg:flex-row gap-8">
          <div className="lg:w-2/3">
            {Object.entries(groupedMenuItems).map(([category, items]) => (
              <div key={category} className="mb-8">
                <h2 className="text-2xl font-semibold mb-4">{category}</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {items.map((item, index) => (
                    <motion.div
                      key={item.id}
                      id={`menu-item-${index}`}
                      className="bg-white p-4 rounded-lg shadow"
                      whileHover={{ scale: 1.05 }}
                    >
                      <h3 className="text-xl font-semibold">{item.name}</h3>
                      <p className="text-gray-600">{item.description}</p>
                      <div className="flex justify-between items-center mt-2">
                        <span className="text-lg font-bold">₹{item.price}</span>
                        <motion.button
                          onClick={() => addToCart(item)}
                          className="bg-green-500 text-white px-4 py-2 rounded-full hover:bg-green-600"
                          whileHover={{ scale: 1.1 }}
                          whileTap={{ scale: 0.9 }}
                        >
                          Add to Cart
                        </motion.button>
                      </div>
                    </motion.div>
                  ))}
                </div>
              </div>
            ))}
          </div>
          <div className="lg:w-1/3">
            <div className="bg-white p-6 rounded-lg shadow sticky top-4" id="cart">
              <h2 className="text-2xl font-semibold mb-4">Your Cart</h2>
              {cart.length === 0 ? (
                <p>Your cart is empty</p>
              ) : (
                <>
                  {cart.map((item) => (
                    <div key={item.id} className="flex justify-between items-center mb-2">
                      <span>{item.name}</span>
                      <div>
                        <span className="mr-2">₹{item.price}</span>
                        <button
                          onClick={() => removeFromCart(item.id)}
                          className="text-red-500 hover:text-red-700"
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  ))}
                  <div className="border-t border-gray-200 mt-4 pt-4">
                    <div className="flex justify-between font-bold">
                      <span>Total</span>
                      <span>₹{getTotalPrice()}</span>
                    </div>
                  </div>
                  <motion.button
                    className="w-full bg-green-500 text-white py-2 rounded-full mt-4 hover:bg-green-600"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    Checkout
                  </motion.button>
                </>
              )}
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default MenuPage;
