import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Link } from 'react-router-dom';
import { ShoppingBag, Trash2, Plus, Minus } from 'lucide-react';

const CartItem = ({ item, removeFromCart, updateQuantity }) => (
  <motion.div
    className="flex justify-between items-center bg-white p-4 rounded-lg shadow mb-4"
    initial={{ opacity: 0, y: -20 }}
    animate={{ opacity: 1, y: 0 }}
    exit={{ opacity: 0, y: -20 }}
  >
    <div className="flex items-center">
      <img src={item.image} alt={item.name} className="w-16 h-16 object-cover rounded mr-4" />
      <div>
        <h3 className="font-semibold">{item.name}</h3>
        <p className="text-gray-600">₹{item.price.toFixed(2)}</p>
      </div>
    </div>
    <div className="flex items-center">
      <button
        onClick={() => updateQuantity(item._id, Math.max(1, item.quantity - 1))}
        className="bg-gray-200 p-1 rounded"
        aria-label="Decrease quantity"
      >
        <Minus size={16} />
      </button>
      <span className="mx-2">{item.quantity}</span>
      <button
        onClick={() => updateQuantity(item._id, item.quantity + 1)}
        className="bg-gray-200 p-1 rounded"
        aria-label="Increase quantity"
      >
        <Plus size={16} />
      </button>
      <button
        onClick={() => removeFromCart(item._id)}
        className="ml-4 text-red-500 hover:text-red-700"
        aria-label="Remove item"
      >
        <Trash2 size={20} />
      </button>
    </div>
  </motion.div>
);

const Cart = ({ cartItems = [], removeFromCart, updateQuantity, clearCart }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="fixed bottom-0 right-0 m-4 z-50">
      <motion.button
        onClick={() => setIsExpanded(!isExpanded)}
        className="bg-green-500 text-white p-4 rounded-full shadow-lg hover:bg-green-600 transition-colors duration-200 focus:outline-none focus:ring-2 focus:ring-green-600 focus:ring-opacity-50"
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
      >
        <ShoppingBag size={24} />
        <span className="absolute top-0 right-0 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center text-xs">
          {cartItems.length}
        </span>
      </motion.button>
      
      <AnimatePresence>
        {isExpanded && (
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.95 }}
            transition={{ duration: 0.2 }}
            className="absolute bottom-16 right-0 bg-gray-100 p-6 rounded-lg shadow-lg w-96"
          >
            <h2 className="text-2xl font-bold mb-4">Your Cart</h2>
            {cartItems.length === 0 ? (
              <p className="text-gray-600">Your cart is empty.</p>
            ) : (
              <>
                <AnimatePresence>
                  {cartItems.map((item) => (
                    <CartItem
                      key={item._id}
                      item={item}
                      removeFromCart={removeFromCart}
                      updateQuantity={updateQuantity}
                    />
                  ))}
                </AnimatePresence>
                <div className="mt-4 pt-4 border-t border-gray-300">
                  <p className="text-xl font-bold">Total: ₹{total.toFixed(2)}</p>
                  <div className="mt-4 flex justify-between">
                    <button
                      onClick={clearCart}
                      className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 transition-colors"
                    >
                      Clear Cart
                    </button>
                    <Link
                      to="/checkout"
                      className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors flex items-center"
                    >
                      <ShoppingBag className="mr-2" size={20} />
                      Checkout
                    </Link>
                  </div>
                </div>
              </>
            )}
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default Cart;
