import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { ShoppingCart, Menu as MenuIcon, MessageSquare } from 'lucide-react';
import { Input } from "@/components/ui/input"
import { Button } from "@/components/ui/button"

const Header = ({ cartItemsCount, onSearch, user, onLogout, toggleChat }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <header className="bg-white shadow-sm sticky top-0 z-50">
      <div className="container mx-auto flex items-center justify-between p-4">
        {/* Logo */}
        <Link to="/" className="text-2xl font-bold text-gray-800 hover:text-blue-600 transition-colors duration-300">
          Curry
        </Link>

        {/* Search Bar */}
        <div className="flex-grow max-w-md mx-4">
          <div className="relative">
            <Input
              type="text"
              placeholder="Search menu items..."
              className="w-full pl-3 pr-10 py-2 border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent rounded-full"
              onChange={(e) => onSearch(e.target.value)}
            />
            <Button
              type="submit"
              className="absolute right-1 top-1/2 transform -translate-y-1/2 bg-green-500 hover:bg-green-600 text-white p-2 rounded-full"
            >
              <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clipRule="evenodd" />
              </svg>
            </Button>
          </div>
        </div>

        {/* Navigation Links */}
        <nav className={`${isMenuOpen ? "block" : "hidden"} md:flex md:items-center md:space-x-6`}>
          <Link to="/" className="menu-item">Home</Link>
          <Link to="/menu" className="menu-item">Menu</Link>
          <Link to="/order" className="menu-item">Order</Link>
          <Link to="/track" className="menu-item">Track</Link>

          {/* User Profile and Cart */}
          {user ? (
            <div className="flex items-center space-x-4">
              <Link to="/profile" className="menu-item">Profile</Link>
              <Button onClick={onLogout} variant="outline">Logout</Button>
            </div>
          ) : (
            <Button asChild variant="primary">
              <Link to="/login">Login</Link>
            </Button>
          )}

          {/* Cart Icon */}
          <Link to="/cart" className="relative inline-block">
            <ShoppingCart className="h-6 w-6 text-gray-700 hover:text-blue-600 transition-colors duration-300" />
            {cartItemsCount > 0 && (
              <span className="absolute -top-2 -right-2 bg-red-500 text-white rounded-full h-5 w-5 flex items-center justify-center text-xs font-bold">
                {cartItemsCount}
              </span>
            )}
          </Link>

          {/* Chat Support */}
          <Button onClick={toggleChat} variant="primary" className="flex items-center">
            <MessageSquare className="h-5 w-5 mr-1" /> Chat
          </Button>
        </nav>

        {/* Mobile Menu Toggle */}
        <Button
          className="md:hidden"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
          aria-label="Toggle menu"
          variant="ghost"
        >
          <MenuIcon />
        </Button>
      </div>
    </header>
  );
};

export default Header;
