import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import gsap from 'gsap';
import { TextPlugin } from 'gsap/TextPlugin';

gsap.registerPlugin(TextPlugin);

const Home = () => {
  const logoRef = useRef(null);
  const heroRef = useRef(null);

  useEffect(() => {
    // Logo animation
    gsap.to(logoRef.current, {
      duration: 2,
      text: {
        value: "Curry",
        delimiter: "",
      },
      repeat: -1,
      yoyo: true,
      ease: "power1.inOut",
    });

    // Hero section animation
    gsap.from(heroRef.current.children, {
      opacity: 0,
      y: 50,
      stagger: 0.2,
      duration: 1,
      ease: "power3.out",
    });
  }, []);

  return (
    <div className="bg-green-100 min-h-screen">
      <div className="container mx-auto px-4 py-16">
        <motion.div
          ref={heroRef}
          className="text-center"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <h1 className="text-6xl font-bold mb-4 text-green-900">
            Welcome to <span ref={logoRef} className="text-green-700"></span>
          </h1>
          <p className="text-xl mb-8 text-gray-800">Delicious vegetarian cuisine at your doorstep</p>
          <Link to="/menu">
            <motion.button
              className="bg-green-600 text-white px-8 py-3 rounded-full text-lg font-semibold hover:bg-green-700 transition-colors"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              Explore Our Menu
            </motion.button>
          </Link>
        </motion.div>

        <div className="mt-16 grid grid-cols-1 md:grid-cols-3 gap-8">
          <motion.div
            className="bg-white p-6 rounded-lg shadow-lg"
            whileHover={{ y: -5 }}
          >
            <h2 className="text-2xl font-semibold mb-4 text-green-800">Fresh Ingredients</h2>
            <p className="text-gray-700">We use only the freshest, locally-sourced ingredients to create our delicious meals.</p>
          </motion.div>
          <motion.div
            className="bg-white p-6 rounded-lg shadow-lg"
            whileHover={{ y: -5 }}
          >
            <h2 className="text-2xl font-semibold mb-4 text-green-800">Fast Delivery</h2>
            <p className="text-gray-700">Our efficient delivery system ensures your food arrives hot and fresh at your doorstep.</p>
          </motion.div>
          <motion.div
            className="bg-white p-6 rounded-lg shadow-lg"
            whileHover={{ y: -5 }}
          >
            <h2 className="text-2xl font-semibold mb-4 text-green-800">Eco-Friendly</h2>
            <p className="text-gray-700">We're committed to sustainability, using eco-friendly packaging for all our deliveries.</p>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default Home;
