import React, { useState, useEffect, useRef } from 'react';
import { useLocation } from 'react-router-dom';
import { motion } from 'framer-motion';
import { gsap } from 'gsap';
import { Package, Truck, Home, CheckCircle, Search, Clock } from 'lucide-react';

const DeliveryStatus = ({ status, estimatedDeliveryTime, deliveryOTP }) => {
  const getStatusIcon = () => {
    switch (status) {
      case 'Order Placed':
        return <Package className="text-purple-500" size={24} />;
      case 'Preparing':
        return <Clock className="text-yellow-500" size={24} />;
      case 'Out for Delivery':
        return <Truck className="text-blue-500" size={24} />;
      case 'Delivered':
        return <CheckCircle className="text-green-500" size={24} />;
      default:
        return null;
    }
  };

  const statusSteps = [
    { icon: Package, label: 'Order Placed', color: 'text-purple-500' },
    { icon: Clock, label: 'Preparing', color: 'text-yellow-500' },
    { icon: Truck, label: 'Out for Delivery', color: 'text-blue-500' },
    { icon: CheckCircle, label: 'Delivered', color: 'text-green-500' }
  ];

  const currentStep = statusSteps.findIndex(step => step.label === status);

  return (
    <motion.div
      className="bg-white p-6 rounded-lg shadow-lg mb-8"
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <h2 className="text-2xl font-bold mb-4 flex items-center text-gray-800">
        {getStatusIcon()}
        <span className="ml-2">Delivery Status</span>
      </h2>
      <div className="flex justify-between mb-4">
        {statusSteps.map((step, index) => (
          <div key={index} className="flex flex-col items-center">
            <div className={`rounded-full p-3 ${index <= currentStep ? step.color : 'bg-gray-300'}`}>
              <step.icon className={`w-6 h-6 ${index <= currentStep ? 'text-white' : 'text-gray-500'}`} />
            </div>
            <span className={`mt-2 text-sm ${index <= currentStep ? step.color : 'text-gray-500'}`}>{step.label}</span>
          </div>
        ))}
      </div>
      <div className="h-2 bg-gray-200 rounded-full">
        <div 
          className="h-full bg-green-500 rounded-full transition-all duration-500 ease-out"
          style={{ width: `${(currentStep / (statusSteps.length - 1)) * 100}%` }}
        />
      </div>
      <p className="text-lg mt-4 text-gray-700">Current Status: <span className="font-semibold text-blue-600">{status}</span></p>
      <p className="text-lg mb-4 text-gray-700">Estimated Delivery Time: <span className="font-semibold text-blue-600">{estimatedDeliveryTime}</span></p>
      {status === 'Out for Delivery' && deliveryOTP && (
        <motion.div
          className="bg-green-100 border-l-4 border-green-500 p-4 mt-4"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ delay: 0.3 }}
        >
          <p className="font-bold text-green-800">Delivery OTP: <span className="text-green-700">{deliveryOTP}</span></p>
          <p className="mt-2 text-sm text-gray-600">Please share this OTP with the delivery agent upon arrival.</p>
        </motion.div>
      )}
    </motion.div>
  );
};

const TrackOrderPage = ({ orderHistory, getOrderStatus }) => {
  const [orderId, setOrderId] = useState('');
  const [orderStatus, setOrderStatus] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const location = useLocation();
  const formRef = useRef(null);
  const statusRef = useRef(null);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const orderIdParam = params.get('orderId');
    if (orderIdParam) {
      setOrderId(orderIdParam);
      handleTrackOrder(orderIdParam);
    }

    // GSAP animation
    gsap.from(formRef.current, {
      opacity: 0,
      y: 50,
      duration: 0.8,
      ease: 'power3.out'
    });
  }, [location]);

  const handleTrackOrder = async (id = orderId) => {
    setLoading(true);
    setError(null);
    try {
      const status = getOrderStatus(id);
      if (status === 'Not Found') {
        setError('Order not found. Please check the order ID and try again.');
      } else {
        const orderDetails = orderHistory.find(order => order.id === id);
        setOrderStatus({
          status: status,
          estimatedDeliveryTime: '30 minutes',
          deliveryOTP: status === 'Out for Delivery' ? Math.floor(1000 + Math.random() * 9000) : null,
          details: orderDetails
        });
      }
      
      // Animate status appearance
      if (statusRef.current) {
        gsap.from(statusRef.current, {
          opacity: 0,
          y: 30,
          duration: 0.6,
          ease: 'power2.out',
          delay: 0.2
        });
      }
    } catch (error) {
      console.error('Error fetching order status:', error);
      setError('Failed to fetch order status. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 bg-gray-50 min-h-screen">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <h1 className="text-4xl font-bold mb-8 text-center text-gray-800">Track Your Order</h1>
        <div ref={formRef} className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-lg mb-8">
          <form onSubmit={(e) => { e.preventDefault(); handleTrackOrder(); }} className="space-y-4">
            <input
              type="text"
              value={orderId}
              onChange={(e) => setOrderId(e.target.value)}
              placeholder="Enter your order ID"
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-gray-700"
              required
            />
            <motion.button
              type="submit"
              className="w-full bg-blue-500 text-white py-2 rounded-md hover:bg-blue-600 transition-colors duration-300 flex items-center justify-center"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              disabled={loading}
            >
              {loading ? 'Tracking...' : (
                <>
                  <Search className="w-5 h-5 mr-2" />
                  Track Order
                </>
              )}
            </motion.button>
          </form>
        </div>
        {loading && <p className="text-center text-gray-600">Loading order status...</p>}
        {error && <p className="text-center text-red-500">{error}</p>}
        {orderStatus && (
          <motion.div
            ref={statusRef}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <DeliveryStatus
              status={orderStatus.status}
              estimatedDeliveryTime={orderStatus.estimatedDeliveryTime}
              deliveryOTP={orderStatus.deliveryOTP}
            />
            {orderStatus.status === 'Delivered' && (
              <div className="text-center bg-white p-6 rounded-lg shadow-lg mb-8">
                <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-4" />
                <p className="text-xl font-semibold text-gray-800">Your order has been delivered!</p>
                <p className="mt-2 text-gray-600">Thank you for choosing our service.</p>
              </div>
            )}
            {orderStatus.details && (
              <div className="mt-8 bg-white p-6 rounded-lg shadow-lg">
                <h2 className="text-2xl font-bold mb-4 text-gray-800">Order Details</h2>
                <p className="text-gray-700"><strong>Order ID:</strong> {orderStatus.details.id}</p>
                <p className="text-gray-700"><strong>Date:</strong> {new Date(orderStatus.details.date).toLocaleString()}</p>
                <p className="text-gray-700"><strong>Total:</strong> ₹{orderStatus.details.total.toFixed(2)}</p>
                <h3 className="text-xl font-semibold mt-4 mb-2 text-gray-800">Items:</h3>
                <ul className="list-disc list-inside text-gray-700">
                  {orderStatus.details.items.map((item, index) => (
                    <li key={index}>{item.name} x {item.quantity}</li>
                  ))}
                </ul>
              </div>
            )}
          </motion.div>
        )}
      </motion.div>
    </div>
  );
};

export default TrackOrderPage;
