import React, { useState, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import { Link } from 'react-router-dom';
import { ShoppingBag, Star, Filter, Search } from 'lucide-react';
import { getMenuItems, submitReview } from '../services/api';

gsap.registerPlugin(ScrollTrigger);

const MenuPage = ({ addToCart, removeFromCart, updateQuantity, cartItems, searchTerm: initialSearchTerm }) => {
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState(initialSearchTerm || '');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 8;

  const fetchMenuItems = useCallback(async () => {
    try {
      setLoading(true);
      const items = await getMenuItems(searchTerm);
      setMenuItems(items);
      setError(null);
    } catch (error) {
      console.error('Error fetching menu items:', error);
      setError('Failed to load menu items. Please try again later.');
    } finally {
      setLoading(false);
    }
  }, [searchTerm]);

  useEffect(() => {
    fetchMenuItems();
  }, [fetchMenuItems]);

  useEffect(() => {
    if (!loading && menuItems.length > 0) {
      menuItems.forEach((_, index) => {
        gsap.from(`#menu-item-${index}`, {
          duration: 1,
          opacity: 0,
          y: 50,
          ease: "power3.out",
          scrollTrigger: {
            trigger: `#menu-item-${index}`,
            start: "top 80%",
          }
        });
      });
    }
  }, [loading, menuItems]);

  const categories = ['All', ...new Set(menuItems.map(item => item.category))];
  const filteredMenuItems = selectedCategory === 'All'
    ? menuItems
    : menuItems.filter(item => item.category === selectedCategory);

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredMenuItems.slice(indexOfFirstItem, indexOfLastItem);

  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleReviewSubmit = async (itemId, reviewData) => {
    try {
      await submitReview({ ...reviewData, menuItemId: itemId });
      // Optionally update UI with the new review
      fetchMenuItems(); // Refetch menu items to get updated reviews
    } catch (error) {
      console.error('Error submitting review:', error);
      setError('Failed to submit review. Please try again later.');
    }
  };

  const handleSearch = (term) => {
    setSearchTerm(term);
    setCurrentPage(1);
  };

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  if (loading) {
    return <div className="text-center mt-8">Loading menu items...</div>;
  }

  if (error) {
    return <div className="text-center mt-8 text-red-500">{error}</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
        <h1 className="text-4xl font-bold mb-8 text-center">Our Menu</h1>

        <SearchBar onSearch={handleSearch} />

        <FilterOptions 
          categories={categories} 
          selectedCategory={selectedCategory} 
          setSelectedCategory={setSelectedCategory} 
        />

        <div className="flex flex-col lg:flex-row gap-8">
          <div className="lg:w-2/3">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {currentItems.map((item, index) => (
                <motion.div 
                  key={item._id} 
                  id={`menu-item-${index}`} 
                  className="bg-white p-4 rounded-lg shadow" 
                  whileHover={{ scale: 1.05 }}
                >
                  <h3 className="text-xl font-semibold">{item.name}</h3>
                  <p>{item.description}</p>
                  <p>Price: ₹{item.price}</p>
                  <button 
                    onClick={() => addToCart(item)} 
                    className="bg-green-500 text-white px-3 py-1 mt-2 rounded"
                  >
                    Add to Cart
                  </button>
                  <ReviewForm onSubmit={(reviewData) => handleReviewSubmit(item._id, reviewData)} />
                </motion.div>
              ))}
            </div>
            <Pagination 
              itemsPerPage={itemsPerPage}
              totalItems={filteredMenuItems.length}
              paginate={paginate}
              currentPage={currentPage}
            />
          </div>

          <div className="lg:w-1/3">
            <h2 className="text-2xl font-semibold mb-4">Cart</h2>
            <div id="cart" className="space-y-4">
              {cartItems.length === 0 ? (
                <p>Your cart is empty.</p>
              ) : (
                cartItems.map((item, index) => (
                  <div key={index} className="bg-gray-100 p-4 rounded-lg">
                    <p>{item.name} - ₹{item.price} x {item.quantity}</p>
                    <button onClick={() => removeFromCart(item._id)} className="text-red-500">Remove</button>
                    <input 
                      type="number" 
                      min="1" 
                      max="6" 
                      value={item.quantity} 
                      onChange={(e) => updateQuantity(item._id, parseInt(e.target.value))} 
                      className="ml-2 w-16 px-2 py-1 border rounded" 
                    />
                  </div>
                ))
              )}
            </div>
            <div className="mt-4 pt-4 border-t border-gray-300">
              <p className="text-xl font-bold">Total: ₹{total.toFixed(2)}</p>
              <div className="mt-4 flex justify-between">
                <Link to="/checkout" className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors flex items-center">
                  <ShoppingBag className="mr-2" size={20} />
                  Proceed to Checkout
                </Link>
              </div>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

const FilterOptions = ({ categories, selectedCategory, setSelectedCategory }) => {
  return (
    <motion.div
      initial={{ opacity: 0, y: -20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="mb-8 bg-white p-4 rounded-lg shadow"
    >
      <div className="flex items-center mb-4">
        <Filter className="mr-2" size={24} />
        <h3 className="text-xl font-semibold">Filter by Category</h3>
      </div>
      <div className="flex flex-wrap gap-2">
        {categories.map((category) => (
          <motion.button
            key={category}
            onClick={() => setSelectedCategory(category)}
            className={`px-4 py-2 rounded-full transition-colors ${
              selectedCategory === category
                ? 'bg-green-500 text-white'
                : 'bg-gray-200 hover:bg-gray-300'
            }`}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {category}
          </motion.button>
        ))}
      </div>
    </motion.div>
  );
};

const Pagination = ({ itemsPerPage, totalItems, paginate, currentPage }) => {
  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(totalItems / itemsPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <nav className="mt-8">
      <ul className="flex justify-center">
        {pageNumbers.map(number => (
          <li key={number} className="mx-1">
            <button
              onClick={() => paginate(number)}
              className={`px-3 py-1 rounded ${
                currentPage === number
                  ? 'bg-green-500 text-white'
                  : 'bg-gray-200 hover:bg-gray-300'
              }`}
            >
              {number}
            </button>
          </li>
        ))}
      </ul>
    </nav>
  );
};

const ReviewForm = ({ onSubmit }) => {
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (rating === 0) {
      setError('Please select a rating.');
      return;
    }
    if (comment.trim() === '') {
      setError('Please write a comment.');
      return;
    }
    setError('');
    onSubmit({ rating, comment });
    setRating(0);
    setComment('');
  };

  return (
    <form onSubmit={handleSubmit} className="mt-4">
      <h3 className="text-lg font-semibold mb-2">Leave a Review</h3>
      <div className="flex mb-2">
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            size={24}
            onClick={() => setRating(star)}
            fill={star <= rating ? 'gold' : 'none'}
            stroke={star <= rating ? 'gold' : 'currentColor'}
            className="cursor-pointer"
          />
        ))}
      </div>
      {error && <p className="text-red-500 mb-2">{error}</p>}
      <textarea
        value={comment}
        onChange={(e) => setComment(e.target.value)}
        placeholder="Write your review..."
        className="w-full px-3 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500 bg-white text-gray-800"
        rows="3"
      ></textarea>
      <button
        type="submit"
        className="mt-2 bg-green-500 text-white px-4 py-2 rounded-lg hover:bg-green-600 transition-colors"
      >
        Submit Review
      </button>
    </form>
  );
};

const SearchBar = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      onSearch(searchTerm);
    }, 300);

    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm, onSearch]);

  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch(searchTerm);
  };

  return (
    <form onSubmit={handleSubmit} className="flex items-center mb-4">
      <input
        type="text"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        placeholder="Search menu items..."
        className="px-4 py-2 border border-gray-300 rounded-l-lg focus:outline-none focus:ring-2 focus:ring-green-500 flex-grow"
      />
      <button
        type="submit"
        className="bg-green-500 text-white px-4 py-2 rounded-r-lg hover:bg-green-600 transition-colors"
      >
        <Search size={20} />
      </button>
    </form>
  );
};

export default MenuPage;
