import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import FilterOptions from '../components/FilterOptions';
import ReviewForm from '../components/ReviewForm';
import { Link } from 'react-router-dom';
import { ShoppingBag } from 'lucide-react';
import { getMenuItems, submitReview } from '../services/api';

gsap.registerPlugin(ScrollTrigger);

const MenuPage = ({ addToCart, removeFromCart, updateQuantity, cartItems, searchTerm }) => {
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMenuItems = async () => {
      try {
        const items = await getMenuItems(searchTerm);
        setMenuItems(items);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching menu items:', error);
        setError('Failed to load menu items. Please try again later.');
        setLoading(false);
      }
    };
    fetchMenuItems();
  }, [searchTerm]);

  useEffect(() => {
    menuItems.forEach((_, index) => {
      gsap.from(`#menu-item-${index}`, {
        duration: 1,
        opacity: 0,
        y: 50,
        ease: "power3.out",
        scrollTrigger: {
          trigger: `#menu-item-${index}`,
          start: "top 80%",
        }
      });
    });
  }, [menuItems]);

  const categories = ['All', ...new Set(menuItems.map(item => item.category))];
  const filteredMenuItems = selectedCategory === 'All'
    ? menuItems
    : menuItems.filter(item => item.category === selectedCategory);
  const total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  const handleReviewSubmit = async (itemId, reviewData) => {
    try {
      await submitReview({ ...reviewData, menuItemId: itemId });
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  if (loading) {
    return <div className="text-center mt-8">Loading menu items...</div>;
  }

  if (error) {
    return <div className="text-center mt-8 text-red-500">{error}</div>;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
        <h1 className="text-4xl font-bold mb-8 text-center">Our Menu</h1>

        <FilterOptions categories={categories} selectedCategory={selectedCategory} setSelectedCategory={setSelectedCategory} />

        <div className="flex flex-col lg:flex-row gap-8">
          <div className="lg:w-2/3">
            {Object.entries(filteredMenuItems.reduce((acc, item) => {
              if (!acc[item.category]) {
                acc[item.category] = [];
              }
              acc[item.category].push(item);
              return acc;
            }, {})).map(([category, items]) => (
              <div key={category} className="mb-8">
                <h2 className="text-2xl font-semibold mb-4">{category}</h2>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {items.map((item, index) => (
                    <motion.div key={item.id} id={`menu-item-${index}`} className="bg-white p-4 rounded-lg shadow" whileHover={{ scale: 1.05 }}>
                      <h3 className="text-xl font-semibold">{item.name}</h3>
                      <p>{item.description}</p>
                      <p>Price: ₹{item.price}</p>
                      <button onClick={() => addToCart(item)} className="bg-green-500 text-white px-3 py-1 mt-2 rounded">Add to Cart</button>
                      <ReviewForm onSubmit={(reviewData) => handleReviewSubmit(item.id, reviewData)} />
                    </motion.div>
                  ))}
                </div>
              </div>
            ))}
          </div>

          <div className="lg:w-1/3">
            <h2 className="text-2xl font-semibold mb-4">Cart</h2>
            <div id="cart" className="space-y-4">
              {cartItems.length === 0 ? (
                <p>Your cart is empty.</p>
              ) : (
                cartItems.map((item, index) => (
                  <div key={index} className="bg-gray-100 p-4 rounded-lg">
                    <p>{item.name} - ₹{item.price} x {item.quantity}</p>
                    <button onClick={() => removeFromCart(item.id)} className="text-red-500">Remove</button>
                    <input type="number" min="1" max="6" value={item.quantity} onChange={(e) => updateQuantity(item.id, parseInt(e.target.value))} className="ml-2 w-16 px-2 py-1 border rounded" />
                  </div>
                ))
              )}
            </div>
            <div className="mt-4 pt-4 border-t border-gray-300">
              <p className="text-xl font-bold">Total: ₹{total.toFixed(2)}</p>
              <div className="mt-4 flex justify-between">
                <Link to="/checkout" className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition-colors flex items-center">
                  <ShoppingBag className="mr-2" size={20} />
                  Proceed to Checkout
                </Link>
              </div>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default MenuPage;
