import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { ShoppingCart, Home, Menu as MenuIcon, Map, User } from 'lucide-react';
import SearchBar from './SearchBar';

const Header = ({ cartItemsCount, onSearch }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <header className="bg-green-600 text-white p-4">
      <div className="container mx-auto flex flex-wrap justify-between items-center">
        <Link to="/" className="text-2xl font-bold">Veg-App</Link>
        
        <button 
          className="lg:hidden"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          <MenuIcon size={24} />
        </button>

        <nav className={`${isMenuOpen ? 'block' : 'hidden'} lg:block w-full lg:w-auto mt-4 lg:mt-0`}>
          <ul className="flex flex-col lg:flex-row space-y-2 lg:space-y-0 lg:space-x-4">
            <li><Link to="/" className="flex items-center"><Home className="mr-1" size={18} /> Home</Link></li>
            <li><Link to="/menu" className="flex items-center"><MenuIcon className="mr-1" size={18} /> Menu</Link></li>
            <li><Link to="/order" className="flex items-center"><ShoppingCart className="mr-1" size={18} /> Order</Link></li>
            <li><Link to="/track" className="flex items-center"><Map className="mr-1" size={18} /> Track</Link></li>
            <li><Link to="/profile" className="flex items-center"><User className="mr-1" size={18} /> Profile</Link></li>
          </ul>
        </nav>

        <div className="w-full lg:w-auto mt-4 lg:mt-0">
          <SearchBar onSearch={onSearch} />
        </div>

        <Link to="/cart" className="flex items-center mt-4 lg:mt-0">
          <ShoppingCart className="mr-1" size={18} />
          <span className="bg-white text-green-600 rounded-full px-2 py-1 text-xs font-bold">
            {cartItemsCount}
          </span>
        </Link>
      </div>
    </header>
  );
};

export default Header;
