import React, { useState } from 'react';
import { motion } from 'framer-motion';
import DeliveryStatus from '../components/DeliveryStatus';

const TrackOrderPage = ({ orderHistory, getOrderStatus }) => {
  const [orderId, setOrderId] = useState('');
  const [orderDetails, setOrderDetails] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    const status = getOrderStatus(orderId);
    if (status !== 'Not Found') {
      const order = orderHistory.find(o => o.id === orderId);
      setOrderDetails({
        status,
        estimatedDeliveryTime: order.estimatedDeliveryTime || 'Not available',
        deliveryOTP: order.deliveryOTP || 'N/A'
      });
    } else {
      setOrderDetails(null);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Track Your Order</h1>
      <form onSubmit={handleSubmit} className="mb-8">
        <input
          type="text"
          value={orderId}
          onChange={(e) => setOrderId(e.target.value)}
          placeholder="Enter your order ID"
          className="w-full p-2 border rounded"
          required
        />
        <button type="submit" className="mt-4 bg-blue-500 text-white px-4 py-2 rounded">
          Track Order
        </button>
      </form>
      {orderDetails ? (
        <DeliveryStatus
          status={orderDetails.status}
          estimatedDeliveryTime={orderDetails.estimatedDeliveryTime}
          deliveryOTP={orderDetails.deliveryOTP}
        />
      ) : (
        <p>No order found with the given ID.</p>
      )}
    </div>
  );
};

export default TrackOrderPage;
